INSERT INTO `aliens_abduction` (`first_name`, `last_name`, `when_it_happened`, `how_long`, `how_many`, `alien_description`, `what_they_did`, `fang_spotted`, `other`, `email`) VALUES
('Sally',	'Jones',	'3 days ago',	'1 day',	'four',	'green with six tentacles',	'We just talked and played with a dog.',	'yes',	'I may have seen your dog. Contact me.',	'sally@gregs-list.net'),
('Sally',	'Jones',	'3 days ago',	'1 day',	'four',	'green with six tentacles',	'We just talked and played with a dog',	'yes',	'I may have seen your dog. Contact me.',	'sally@gregs-list.net'),
('Elspeth',	'Stalter-Clouse',	'12 days ago',	'14 hours',	'6',	'tall and thin, with funky glasses',	'watched me eat a plate of spaghetti',	'no',	'Fang is a funny name for a pug.',	'estalterclouse@madisoncollege.edu'),
('Mimsy ',	'Borogove',	'January 22nd, 1982',	'4 years',	'too many',	'they were like clouds of grey mist',	'talked to me, removed my spleen, and implanted a chip in my brain',	'no',	'I think Fang will be OK, but you might never see him again. ',	'momeraths@outgrabe.net'),
('Steely',	'Dan',	'1972',	'10 hours',	'8 or 9',	'they were tiny and shiny, like little metal beetles',	'they asked me to sing a song',	'no',	'nope. sorry dude. hope you find your dog.',	'saxophone@learntoworkit.com'),
('Lamby',	'Lambykins',	'24 hours ago',	'45 minutes',	'three',	'they were green with tentacles, like an octopus',	'nothing, really. I think my abduction was maybe a mistake?',	'yes',	'one of them passed me with a dog on a leash.',	'iamalamb@iamnotalamb.com'),
('Oliver Jean',	'Ball',	'last month',	'36 hours',	'9',	'they looked just like me ',	'they told me my mothership is on the way',	'no',	'Forget about your dog. Get a cat instead.',	'ollieball@gmail.com'),
('Bo',	'Peep',	'5 years ago',	'48 hours',	'42',	'they were toddler-sized, but with lots of eyes.',	'I watched them have a feast. ',	'no',	'Nope.',	'haslost@hersheep.com'),
('Marty',	'Pants',	'15 days ago',	'6 days',	'10',	'they were great big green octopuses',	'they made me brush a dog. I am super allergic, so...not fun.',	'yes',	'I think it was Fang; hard to tell with the itchy eyes, though.',	'smartypants@gmail.com'),
('Boaty',	'McBoatFace',	'12 days ago',	'14 hours',	'20-30',	'lots of arms/legs/whatevers. green. slimy. big heads.',	'they showed me a dog and asked me if I thought it was cute. I did NOT think it was.',	'yes',	'I am 99.9% sure I saw your dog. It seemed pretty happy up there...are you sure you want it back?',	'thetruth@isoutthere.com'),
('Tweedle',	'Dum',	'78 hours ago',	'2 hours',	'32',	'super tiny. shaped like krill.',	'they tickled my feet. ',	'no',	'Sorry I cannot be of more use! ',	'dum@thetweedles.com'),
('Tweedle',	'Dee',	'90 hours ago',	'5 hours',	'one',	'gargantuan! like a ginormous shrimp. ',	'it made me hungry just looking at it. ',	'no',	'Good luck finding Fang!',	'deedee@thetweedles.com'),
('Old Woman',	'Who Lived In A Shoe',	'12 days ago',	'46 hours',	'way too many',	'they were huge and green, like octopus-people.',	'they showed me a puppy and asked me how it worked.',	'yes',	'I absolutely saw Fang. I have no doubts in my mind about it. ',	'toomanykids@helpme.com'),
('Little Boy',	'Blue',	'7 days ago',	'4 days',	'a handful',	'they looked like cows with snail shells. ',	'they gave me a pail of slimy milk to drink.',	'no',	'I was NOT sleeping under that haystack. I was looking after the sheep! I swear!',	'fastasleep@underthehaystack.com'),
('Little Miss',	'Muffet',	'7 days ago',	'6 days',	'7 or 8',	'they were sticky and gooey and green with tentacles',	'they asked me what a tuffet was and made me walk a dog.',	'yes',	'Fang is adorable, but tuffets are stupid. They are made for feet. Never sit on a tuffet.',	'tuffet@whatisatuffetanyway.com'),
('Dead',	'Pool',	'today',	'4 days',	'1',	'Looked like Peter Parker',	'made me dance dub step',	'yes',	'nothing that does not involve profanity',	'iKillBaddies@xmen.com'),
('Dead\'s',	'Pool',	'today',	'4 days',	'1',	'Looked like Peter Parker',	'made me dance dub step',	'yes',	'nothing that does not involve profanity',	'iKillBaddies@xmen.com'),
