INSERT INTO `boy_blue` (`storyid`, `adjective`, `verb`, `noun_1`, `noun_2`, `noun_3`, `noun_4`, `noun_5`, `full_story`) VALUES
(21,	'Dinky',	'smell',	'eyeball',	'trough',	'USS Enterprise',	'milk dud',	'sweatshirt',	'Dinky Boy Blue <br />Come smell your eyeball!<br />the trough is in the meadow, <br />the USS Enterprise is in the corn.<br />Where is the milk dud who looks after the sheep?<br />He is under the sweatshirt, fast asleep.'),
(22,	'Janky',	'cook',	'krill',	'giant bell',	'cyclops',	'magical unicorn',	'thimble',	'Janky Boy Blue <br />Come cook your krill!<br />the giant bell is in the meadow, <br />the cyclops is in the corn.<br />Where is the magical unicorn who looks after the sheep?<br />He is under the thimble, fast asleep.'),
(23,	'Sparkly',	'boil',	'snail',	'slug',	'goat',	'president of the united states',	'Mona Lisa',	'Sparkly Boy Blue <br />Come boil your snail!<br />the slug is in the meadow, <br />the goat is in the corn.<br />Where is the president of the united states who looks after the sheep?<br />He is under the Mona Lisa, fast asleep.');
INSERT INTO `miss_muffet` (`storyid`, `adjective`, `noun_1`, `verb_ing`, `plural_noun_1`, `plural_noun_2`, `noun_2`, `verb_past`, `full_story`) VALUES
(32,	'Humungous',	'Oat',	'shouting',	'snakes',	'mannequins',	'tarantula',	'clubbed',	'Humungous Miss Muffet <br />Sat on a(n) Oat<br />shouting her snakes and mannequins.<br />Along came a tarantula<br />Which clubbed down beside her<br />And frightened Miss Muffet away.'),
(33,	'Horrid',	'Froot Loop',	'Smelling',	'mufflers',	'steering wheels',	'snorkel',	'roundhouse-kicked',	'Horrid Miss Muffet <br />Sat on a(n) Froot Loop<br />Smelling her mufflers and steering wheels.<br />Along came a snorkel<br />Which roundhouse-kicked down beside her<br />And frightened Miss Muffet away.'),
(34,	'Neon',	'Porg',	'climbing',	'potholders',	'mice',	'yarn',	'swam',	'Neon Miss Muffet <br />Sat on a(n) Porg<br />climbing her potholders and mice.<br />Along came a yarn<br />Which swam down beside her<br />And frightened Miss Muffet away.'),
(35,	'Clever',	'Sprite',	'Darting',	'Trees',	'tree stumps',	'polar  bear',	'clucked',	'Clever Miss Muffet <br />Sat on a(n) Sprite<br />Darting her Trees and tree stumps.<br />Along came a polar  bear<br />Which clucked down beside her<br />And frightened Miss Muffet away.'),
(36,	'scruffy',	'pocket',	'blending',	'crumpets',	'scribes',	'loon',	'galumphed',	'scruffy Miss Muffet <br />Sat on a(n) pocket<br />blending her crumpets and scribes.<br />Along came a loon<br />Which galumphed down beside her<br />And frightened Miss Muffet away.');
INSERT INTO `shoe_woman` (`storyid`, `noun`, `plural_noun_1`, `plural_noun_2`, `plural_noun_3`, `adverb`, `full_story`) VALUES
(5,	'old shoe',	'musty potatoes',	'deviled eggs',	'more potatoes',	'forlornly',	'There was an old woman who lived in a old shoe.<br />She had so many musty potatoes, she did not know what to do.<br />She gave them some deviled eggs without any more potatoes;<br />Then whipped them all forlornly and put them to bed.<br />'),
(6,	'cuckoo clock',	'grubs',	'smurfs',	'rusty unicycles',	'blithely',	'There was an old woman who lived in a cuckoo clock.<br />She had so many grubs, she did not know what to do.<br />She gave them some smurfs without any rusty unicycles;<br />Then whipped them all blithely and put them to bed.<br />'),
(7,	'butterfly',	'cereals',	'beans',	'jewel-encrusted toilets',	'dangerously',	'There was an old woman who lived in a butterfly.<br />She had so many cereals, she did not know what to do.<br />She gave them some beans without any jewel-encrusted toilets;<br />Then whipped them all dangerously and put them to bed.<br />');
